<?phpclass parseUrl {    public static function get_page($pars) {        $pars_array = explode("/", $pars);        if (empty($pars_array[count($pars_array) - 1]))            unset($pars_array[count($pars_array) - 1]);        $page = $pars_array[0];        if (empty($page))            $page = "home";        elseif (count($pars_array) == 1)            $page = $pars_array[count($pars_array) - 1];        return $page;    }    public static function get_pars($pars) {        $return_pars = array();        $pars_array = explode("/", $pars);        if (empty($pars_array[count($pars_array) - 1]))            unset($pars_array[count($pars_array) - 1]);        for ($i = 1; $i < sizeof($pars_array); $i+=2)            $return_pars[$pars_array[$i]] = $pars_array[$i + 1];        if (count($pars_array) == 2)            $return_pars[] = $pars_array[1];        return array_filter($return_pars);    }    public static function pars_var_nr($pars) {        $return_pars = array();        $pars_array = explode("/", $pars);        if (empty($pars_array[count($pars_array) - 1]))            unset($pars_array[count($pars_array) - 1]);        return count($pars_array);    }    public static function friendlyURL($string) {        $string = preg_replace("`\[.*\]`U", "", $string);        $string = preg_replace('`&(amp;)?#?[a-z0-9]+;`i', '-', $string);        $string = htmlentities($string, ENT_COMPAT, 'utf-8');        $string = preg_replace("`&([a-z])(acute|uml|circ|grave|ring|cedil|slash|tilde|caron|lig|quot|rsquo);`i", "\\1", $string);        $string = preg_replace(array("`[^a-z0-9]`i", "`[-]+`"), "-", $string);        return strtolower(trim($string, '-'));    }}?>