<?phpclass shortString {    public static function convert($string, $length, $words=true, $replacer='  ...') {        if ($words) {            if (strlen($string) > $length)                return (preg_match('/^(.*)\W.*$/', substr($string, 0, $length + 1), $matches) ? $matches[1] : substr($string, 0, $length)) .' '. $replacer;            return $string;        }else {            $length-=strlen($replacer);            return (strlen($string) > $length) ? substr($string, 0, $length) . $replacer : $string;        }    }}?>